        if ($buffer) { ?>
        
        <script>
        parent.postMessage(<?php
            echo trim($buffer);
        ?>, '*');
        </script>

        
        
        
        
        
        
        
        
        

        
        
        
        
        
        
<?php
        }

        //sleep(0.2);

        $newTime = time();
        if ($newTime - $time >= 1) {
            $time = $newTime; ?>
       
